mapper.insert:

INSERT
	INTO membership(
    	userId,
    	businessId
    	)
	VALUES(
		(select userId from user where userTel=#{userTel}),
    	#{businessId}
    )

mapper.select:

select count(*) count
from stamplist
where couponNum is null
and userId=#{userId}
and businessId =#{businessId}

mapper.update:

update stamplist
set couponNum=#{couponNum}
where
	businessId=#{businessId}
and
	userId=#{userId}
and
	couponNum is null
limit #{stampNeed}
